INSERT INTO `works` VALUES ('342', 'video', '心流', null, null, '在很多的四面空间以及多面空间，甚至弧度空间，作者选择了三角体的造型空间做为研究对象，三角体空间里面在视觉成像上有很大的研究难度，首先是在影射在斜面上的视觉效果，以及镜面折射出的多维度空间效果。另外除了一面是影射投影的屏幕外，另外三面墙是以镜面为主的墙面，可以折射多面镜像效果，让观众在其中体验不同的沉浸空间。空间高为2.8米，宽为2.5米，足可以容纳三到四人的浏览面积，沉浸空间内的影像长为三分钟，分三段不同的视觉效果，不同的音乐节奏，这个是本作品的亮点，如何在固定空间中找到更多的多维空间，难度就在影像动画的效果。', 'tencent', 'u0399lftsnl', '0', '0', '346', '2017-05-05 13:43:27', '2017-05-05 13:43:27', '0', '100', '100', '100', '0', '0', 'http://ate.91ant.com/Public/common/default/tencent_video_cover.jpg', null);
