INSERT INTO `works` VALUES ('609', 'photo', '老同学牛轧糖系列包装设计', '梁陈蠂', '', '老同学包装采用复古漫画风的表现形式,将童年趣事,欢笑电影,娱乐游戏相结合,打造不一样的牛轧糖风格,生动的漫画包装别有一番滋味。老同学~老回忆~老情怀~', 'tencent', '', '0', '0', '592', '2017-06-03 09:20:08', '2017-08-07 10:47:48', '0', '100', '100', '100', '0', '0', 'http://ate.91ant.com/Public/common/works/0/0/609/1488.png', '老同学包装采用复古漫画风的表现形式,将童年趣事,欢笑电影,娱乐游戏相结合,打造不一样的牛轧糖风格,生动的漫画包装别有一番滋味。老同学~老回忆~老情怀~<p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/609/1488.png\' /></p>');
INSERT INTO `works` VALUES ('610', 'photo', '天鸡盒', '梁陈蠂', '', '毕业作品拟名为《天鸡盒》 。《天鸡盒》是一款以“鸡”为主题的春节年品，以惠州农民画为创作原型，加入了树木画、剪纸等艺术形式，为新年增添一些新花样。春节年品包含对联、红包、福字、台历等内容，可满足对新年年品的多种使用需求。', 'tencent', '', '0', '0', '594', '2017-06-03 09:21:34', '2017-08-07 10:47:25', '1', '100', '100', '100', '0', '0', 'http://ate.91ant.com/Public/common/works/0/0/610/1489.png', '毕业作品拟名为《天鸡盒》 。《天鸡盒》是一款以“鸡”为主题的春节年品，以惠州农民画为创作原型，加入了树木画、剪纸等艺术形式，为新年增添一些新花样。春节年品包含对联、红包、福字、台历等内容，可满足对新年年品的多种使用需求。<p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/610/1489.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/610/1490.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/610/1491.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/610/1492.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/610/1493.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/610/1494.png\' /></p>');
INSERT INTO `works` VALUES ('611', 'photo', '创意八卦品牌设计', '朱展鸿', '', '年轻人是社会的生力军，“衣食住行”，衣是排在第一位的，所以从服装\r\n作为传承和弘扬传统文化的宣传是最适合不过的渠道。现在年轻人喜欢品牌\r\n大部分都是外国品牌，中国自己的服装品牌很少有年轻人喜欢。以往的中国\r\n牌子追求价格上的便宜，而不追求品牌的质量，所以就出现了一大堆“地摊\r\n货”，使得中国的服装品牌始终没法受年轻人喜欢。\r\n        八卦太极文化是年轻人比较喜欢的中国文化之一。流行趋势与传统文化\r\n以新的形式更好的结合，才能更好的宣传和传承传统文化。', 'tencent', '', '0', '0', '595', '2017-06-03 09:23:07', '2017-08-07 10:47:08', '0', '100', '100', '100', '0', '0', 'http://ate.91ant.com/Public/common/works/0/0/611/1495.png', '年轻人是社会的生力军，“衣食住行”，衣是排在第一位的，所以从服装\r\n作为传承和弘扬传统文化的宣传是最适合不过的渠道。现在年轻人喜欢品牌\r\n大部分都是外国品牌，中国自己的服装品牌很少有年轻人喜欢。以往的中国\r\n牌子追求价格上的便宜，而不追求品牌的质量，所以就出现了一大堆“地摊\r\n货”，使得中国的服装品牌始终没法受年轻人喜欢。\r\n        八卦太极文化是年轻人比较喜欢的中国文化之一。流行趋势与传统文化\r\n以新的形式更好的结合，才能更好的宣传和传承传统文化。<p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/611/1495.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/611/1496.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/611/1497.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/611/1498.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/611/1499.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/611/1500.png\' /></p>');
