INSERT INTO `works` VALUES ('672', 'video', 'Nina(妮娜)', null, null, '妮娜是个活泼可爱调皮的小女孩，生活在一个彩虹岛上，妮娜有个小毛病喜欢撒谎，有一天她因为贪玩偷了禁林的圣果，还欺骗了守护禁林的梅花鹿，激怒了彩虹岛的霓婆婆，为了惩罚妮娜，霓婆婆因此给妮娜下了个咒语，只要一撒谎，妮娜头就会变变大。', 'tencent', 'i0505xv0uo3', '0', '0', '609', '2017-06-12 13:54:37', '2017-06-12 13:54:37', '0', '100', '100', '100', '0', '0', 'http://ate.91ant.com/Public/common/default/tencent_video_cover.jpg', null);
