INSERT INTO `works` VALUES ('949', 'photo', '老年互动玩具设计', '', '廖树林', '\r\n本次设计是通过增强产品互动性来增强益智产品的易学性，降低产品的使用难度，提高趣味性。考虑老年人自身情况和接受能力，使用规则尽可能的简单，容易上手。增强产品的反馈意识，缓解智力退化。\r\n', 'tencent', '', '0', '0', '1149', '2017-06-21 11:26:19', '2017-08-07 11:05:17', '0', '100', '100', '100', '0', '0', 'http://ate.91ant.com/Public/common/works/0/0/949/2655.png', '\r\n本次设计是通过增强产品互动性来增强益智产品的易学性，降低产品的使用难度，提高趣味性。考虑老年人自身情况和接受能力，使用规则尽可能的简单，容易上手。增强产品的反馈意识，缓解智力退化。\r\n<p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/949/2655.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/949/2656.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/949/2657.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/949/2658.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/949/2659.png\' /></p>');
