INSERT INTO `works` VALUES ('952', 'photo', '童趣响应', '韩瑞丹、张恒懿', '廖树林', ' \r\n针对学龄初期儿童，结合教育教学需求而设计的实体类儿童科教类玩具。基于先有的科教助教用具进行整合与再设计，具备美学美感，提供良好的审美环境，具有高度的可玩性与科教意义，引导儿童从电子娱乐设备上转移视线，激发儿童对于现实世界的探索欲望。', 'tencent', '', '0', '0', '1146', '2017-06-21 11:30:32', '2017-08-07 11:02:59', '0', '100', '100', '100', '0', '0', 'http://ate.91ant.com/Public/common/works/0/0/952/2669.png', ' \r\n针对学龄初期儿童，结合教育教学需求而设计的实体类儿童科教类玩具。基于先有的科教助教用具进行整合与再设计，具备美学美感，提供良好的审美环境，具有高度的可玩性与科教意义，引导儿童从电子娱乐设备上转移视线，激发儿童对于现实世界的探索欲望。<p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/952/2669.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/952/2670.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/952/2671.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/0/952/2672.png\' /></p>');
