INSERT INTO `works` VALUES ('1017', 'photo', '咚咚锵', '周润萍、刘敏聪、李子聪、梁诗铭、林国旺、林钰媛', '高嘉敏', '选择醒狮在毕业设计作为主题，是希望通过本设计使广东岭南文化的醒狮文化特色这一名间艺术得到更好的宣传、发扬和继承。同时我们运用“狮头人身”的设计形象生动的将其拟人化，更加亲切和贴切于我们的生活当中，将醒狮作人，让其附有更多的生命力和生活气息。同时也打破传统醒狮形象加上了为其设计的现代元素，表达与时俱进的观念。', 'tencent', '', '0', '0', '1189', '2017-06-30 14:38:08', '2017-08-07 10:21:57', '0', '100', '100', '100', '0', '0', 'http://ate.91ant.com/Public/common/works/0/1/1017/2947.png', '选择醒狮在毕业设计作为主题，是希望通过本设计使广东岭南文化的醒狮文化特色这一名间艺术得到更好的宣传、发扬和继承。同时我们运用“狮头人身”的设计形象生动的将其拟人化，更加亲切和贴切于我们的生活当中，将醒狮作人，让其附有更多的生命力和生活气息。同时也打破传统醒狮形象加上了为其设计的现代元素，表达与时俱进的观念。<p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1017/2947.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1017/2948.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1017/2949.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1017/2950.png\' /></p>');
INSERT INTO `works` VALUES ('1018', 'photo', '华派厨房调味罐系列', '', '戴丹、郭肖蕾', '灵感来自中国的四大发明之一，活字印刷术。调味瓶利用活字印刷的外形，作为瓶身；利用顶部的的“反字”，作为调味瓶里调料味道的识别符号。融合了中国风的独特造型，展现有高雅趣味的汉字之美，让调味瓶成为餐桌上的一道风景。\r\n', 'tencent', '', '0', '0', '1190', '2017-06-30 14:53:33', '2017-08-07 10:21:24', '0', '100', '100', '100', '0', '0', 'http://ate.91ant.com/Public/common/works/0/1/1018/2951.png', '灵感来自中国的四大发明之一，活字印刷术。调味瓶利用活字印刷的外形，作为瓶身；利用顶部的的“反字”，作为调味瓶里调料味道的识别符号。融合了中国风的独特造型，展现有高雅趣味的汉字之美，让调味瓶成为餐桌上的一道风景。\r\n<p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1018/2951.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1018/2952.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1018/2953.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1018/2954.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1018/2955.png\' /></p>');
