INSERT INTO `works` VALUES ('1271', 'photo', '《窗》', '', '陈少炜', '\"     玫瑰花窗，是哥特式建筑的特色之一，指中世纪教堂正门上方的大圆形窗，内呈放射状。\r\n     其视觉效果方面的追求令我感到神奇又喜爱。故而以玫瑰花窗为主题，运用到毕业设计中。\"\r\n', 'tencent', '', '0', '0', '1504', '2017-07-14 09:51:45', '2017-08-07 10:07:44', '1', '100', '100', '100', '0', '0', 'http://ate.91ant.com/Public/common/works/0/1/1271/3396.png', '&quot;     玫瑰花窗，是哥特式建筑的特色之一，指中世纪教堂正门上方的大圆形窗，内呈放射状。\r\n     其视觉效果方面的追求令我感到神奇又喜爱。故而以玫瑰花窗为主题，运用到毕业设计中。&quot;\r\n&lt;p style=\'text-align: center\'&gt;&lt;img src=\'http://ate.91ant.com/Public/common/works/0/1/1271/3396.png\' /&gt;&lt;/p&gt;&lt;p style=\'text-align: center\'&gt;&lt;img src=\'http://ate.91ant.com/Public/common/works/0/1/1271/3397.png\' /&gt;&lt;/p&gt;&lt;p style=\'text-align: center\'&gt;&lt;img src=\'http://ate.91ant.com/Public/common/works/0/1/1271/3398.png\' /&gt;&lt;/p&gt;&lt;p style=\'text-align: center\'&gt;&lt;img src=\'http://ate.91ant.com/Public/common/works/0/1/1271/3399.png\' /&gt;&lt;/p&gt;');
INSERT INTO `works` VALUES ('1272', 'photo', '《渔羽》', '', '张丹丹，蔡丹丹，罗正文', '\" 金鱼代表着幸福和自主，不受地位约束的舒适之感，而《渔羽》中的渔，\r\n取之于鱼的谐音，渔在古文中有句话写道：“授人以鱼，不如授人予渔”，\r\n说的是传授给人既有知识，不如授给人学习知识的方法。\r\n而羽，取之于凤凰的羽毛，凤凰在远古图腾时代被视为神鸟而\r\n予崇拜，作为百鸟之王，用来象征明君的威望，羽毛极美，象征美丽人生。\r\n这系列服装给人一种优雅，飘逸潇洒的感觉，裙摆犹如金鱼的尾巴，灵活自如，\r\n婀娜多姿。 \"\r\n', 'tencent', '', '0', '0', '1474', '2017-07-14 09:52:50', '2017-08-07 10:07:27', '0', '100', '100', '100', '0', '0', 'http://ate.91ant.com/Public/common/works/0/1/1272/3400.png', '&quot; 金鱼代表着幸福和自主，不受地位约束的舒适之感，而《渔羽》中的渔，\r\n取之于鱼的谐音，渔在古文中有句话写道：“授人以鱼，不如授人予渔”，\r\n说的是传授给人既有知识，不如授给人学习知识的方法。\r\n而羽，取之于凤凰的羽毛，凤凰在远古图腾时代被视为神鸟而\r\n予崇拜，作为百鸟之王，用来象征明君的威望，羽毛极美，象征美丽人生。\r\n这系列服装给人一种优雅，飘逸潇洒的感觉，裙摆犹如金鱼的尾巴，灵活自如，\r\n婀娜多姿。 &quot;\r\n&lt;p style=\'text-align: center\'&gt;&lt;img src=\'http://ate.91ant.com/Public/common/works/0/1/1272/3400.png\' /&gt;&lt;/p&gt;&lt;p style=\'text-align: center\'&gt;&lt;img src=\'http://ate.91ant.com/Public/common/works/0/1/1272/3401.png\' /&gt;&lt;/p&gt;&lt;p style=\'text-align: center\'&gt;&lt;img src=\'http://ate.91ant.com/Public/common/works/0/1/1272/3402.png\' /&gt;&lt;/p&gt;&lt;p style=\'text-align: center\'&gt;&lt;img src=\'http://ate.91ant.com/Public/common/works/0/1/1272/3403.png\' /&gt;&lt;/p&gt;');
