INSERT INTO `works` VALUES ('1659', 'photo', 'Human and nature', '张伟钦', '陈梦佳、陈欢、陈韵蕾', '<p>&nbsp;以“世界自然基金会”的保护濒临灭绝动物种类、协调人类与自然的宗旨，结合铁和绳子等人类社会活动产生的物品根据濒临灭绝动物的造型所制作的装置艺术宣传世界自然基金会的“保护濒临灭绝动物”口号</p>', 'tencent', '', '0', '0', '1824', '2017-12-28 13:29:11', '2017-12-28 13:35:55', '0', '100', '100', '100', '0', '1', 'http://ate.91ant.com/Public/common/works/0/1/1659/4384.png', '<p>&nbsp;以“世界自然基金会”的保护濒临灭绝动物种类、协调人类与自然的宗旨，结合铁和绳子等人类社会活动产生的物品根据濒临灭绝动物的造型所制作的装置艺术宣传世界自然基金会的“保护濒临灭绝动物”口号</p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1659/4384.png\' /></p>');
INSERT INTO `works` VALUES ('1660', 'photo', '大鱼', '徐坚毅', '陈梦佳、陈欢、陈韵蕾', '<p>以海洋元素为主题的装置艺术， &nbsp;海洋元素与现代工艺相结合。亚克力装饰工艺更为突出效果，给人焕然一新的视觉效果。主要运用在高端大型的室内工装空间。</p>', 'tencent', '', '0', '0', '1826', '2017-12-28 13:30:12', '2017-12-28 13:32:25', '0', '100', '100', '100', '0', '1', 'http://ate.91ant.com/Public/common/works/0/1/1660/4385.png', '<p>以海洋元素为主题的装置艺术， &nbsp;海洋元素与现代工艺相结合。亚克力装饰工艺更为突出效果，给人焕然一新的视觉效果。主要运用在高端大型的室内工装空间。</p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1660/4385.png\' /></p>');
