INSERT INTO `works` VALUES ('1653', 'photo', '山水之缘', '张明雪,唐积瑜', '陈梦佳、陈欢、陈韵蕾', '<p>&nbsp;山水之缘---是以山水画为主题图案的一个新中式空间，在用料上以粗糙的麻绳为设计元素。加上绚丽的金色为辅助。让单一的水墨画打破陈规，展示多彩的山水韵味，从而展现一个舒适自在的空间氛围。</p>', 'tencent', '', '0', '0', '1808', '2017-12-28 13:20:55', '2017-12-28 13:37:02', '0', '100', '100', '100', '0', '1', 'http://ate.91ant.com/Public/common/works/0/1/1653/4378.png', '<p>&nbsp;山水之缘---是以山水画为主题图案的一个新中式空间，在用料上以粗糙的麻绳为设计元素。加上绚丽的金色为辅助。让单一的水墨画打破陈规，展示多彩的山水韵味，从而展现一个舒适自在的空间氛围。</p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1653/4378.png\' /></p>');
INSERT INTO `works` VALUES ('1697', 'photo', '白', '', '肖机灵', '<p>追求简约而不空洞，时尚而不过度修饰的北欧设计，白色是最能激发想象力的颜色，它的质地能强有力地唤起任何物体的物质性，也让人能够越过诸色纷呈的干扰，直接关注设计本身</p>', 'tencent', '', '0', '0', '1786', '2017-12-28 16:35:10', '2017-12-28 18:08:06', '0', '100', '100', '100', '0', '1', 'http://ate.91ant.com/Public/common/works/0/1/1697/4414.png', '<p>追求简约而不空洞，时尚而不过度修饰的北欧设计，白色是最能激发想象力的颜色，它的质地能强有力地唤起任何物体的物质性，也让人能够越过诸色纷呈的干扰，直接关注设计本身</p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1697/4414.png\' /></p>');
