INSERT INTO `works` VALUES ('1797', 'photo', '铭源茶室', '陈宗翰、林建洪', '李文东／林子斌', '<p>设计概念：本案设计师希望打破这种城市的浮躁与乏味，运用现代的表现手法重新呈现岭南原有的建筑印记，运用广府建筑中极具特色的青砖、黛瓦两种材料，回归朴实、简约，来体现岭南建筑的简朴雅素的特点，让生活在快节奏城市的人们多一个净化心灵、消除浮躁的空间。</p>', 'tencent', '', '0', '0', '2015', '2017-12-29 14:39:23', '2017-12-29 14:40:53', '0', '100', '100', '100', '0', '1', 'http://ate.91ant.com/Public/common/works/0/1/1797/4682.png', '<p>设计概念：本案设计师希望打破这种城市的浮躁与乏味，运用现代的表现手法重新呈现岭南原有的建筑印记，运用广府建筑中极具特色的青砖、黛瓦两种材料，回归朴实、简约，来体现岭南建筑的简朴雅素的特点，让生活在快节奏城市的人们多一个净化心灵、消除浮躁的空间。</p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1797/4682.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1797/4683.png\' /></p><p style=\'text-align: center\'><img src=\'http://ate.91ant.com/Public/common/works/0/1/1797/4684.png\' /></p>');
